import { Link, NavLink } from 'react-router-dom';
import classNames from 'classnames';
import './index.scss';
import Logo1 from '../../assets/images/logo1.png';
import Logo2 from '../../assets/images/logo2.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faHome, faUser } from '@fortawesome/free-solid-svg-icons';

// List of available animations from animate.css
const animations = [
    'bounce',
    'flash',
    'pulse',
    'rubberBand',
    'shake',
    'swing',
    'tada',
    'wobble',
];

const randomAnimation = animations[Math.floor(Math.random() * animations.length)]; // Pick a random animation

const Sidebar = () => (
    <div className='nav-bar'>
        <Link className={classNames('logo', 'animated', randomAnimation)} to='/'> {/* Add the random animation class here */}
            <img src={Logo1} alt="logo1" />
            <img className="sublogo" src={Logo2} alt="logo" />
        </Link>
        <nav>
            <NavLink exact activeClassName="active" to="/">
                <FontAwesomeIcon icon={faHome} color= "#0e4e86" />
            </NavLink>

            <NavLink exact activeClassName="active" className="about-link" to="/about">
                <FontAwesomeIcon icon={faUser} color= "#0e4e86" />
            </NavLink>

            <NavLink exact activeClassName="active" className="contact-link" to="/contact">
                <FontAwesomeIcon icon={faEnvelope} color= "#0e4e86" />
            </NavLink>
        </nav>
    </div>
);

export default Sidebar;
