import './index.scss'
import AnimatedLetters from '../AnimatedLetters'
import Loader from 'react-loaders'
import './index.scss'
import { useEffect, useState } from 'react'



const About = () => { 
    const [letterClass, setLetterClass] = useState('text-animate')
    useEffect(() => {
        return setTimeout(() => {
          setLetterClass('text-animate-hover')
        }, 3000)
      }, [])

    return (
        <div className='container about-page'>
         <Loader type="pacman" />

            <div className='text-zone'>
                <h1>
                    <AnimatedLetters 
                    letterClass={letterClass}
                    strArray = {['O','u','r,','','s','t','o','r','y']}
                    idx={15}
                    />
                </h1>
                <p>
                We want to connect loving families with their perfect furry companions while upholding the highest standards of responsible breeding and animal welfare.
                </p>
                <p>
                Each puppy that leaves our care is not just a pet but a cherished member of a new family.
                </p>
                <p>
                Whether you're searching for your next furry family member or simply have questions about our breeding practices, we invite you to reach out to us.
                </p>
                <p>
                Together, let's find the perfect dog to bring joy, companionship, and endless wagging tails into your life.
                </p>
            </div>
        </div>

    )
}

export default About
