import React from 'react';
import dogs from '../../data/dogs.json';

const Adoption = () => {
    const renderAdoption = () => {
        return (
            <div className="images-container">
                {dogs.map((dog, idx) => (
                    <div className="image-box" key={idx}>
                        <img 
                            src={dog.cover}
                            className="dogs-image"
                            alt={dog.title}
                        />
                        <div className="content">
                            <p className="title">{dog.title}</p>
                            <h4 className="description">{dog.description}</h4>
                            <button 
                                className="btn"
                                onClick={() => window.open(dog.url)}
                            >
                                View
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        );
    };

    return (
        <div className="container adoption-page">
            <h1 className="page-title">Adopt a Dog</h1>
            <div>{renderAdoption()}</div>
        </div>
    );
};

export default Adoption;
